SetTitle(Signatures)
Div(Class: content-wrapper){
Div(Class: pull-right){
        LinkPage(Body: Em(Class: icon icon-plus) Add new, Page: Create_Signatures, Class: ml btn-tool)
    }.Style(
        height: 45px;
        padding-top: 10px;
        a {
            float: right;
        }
    )
    DBFind("signatures",Source:src_sign).Columns("id,name,value,value->title,conditions").Limit(250).Custom(edit_sign){
        Button(Edit,Class: btn btn-primary,Page:Edit_Signature,PageParams:"Sign_id=#id#,Sign_name=#name#,Sign_text=#value.title#,Sign_cond=#conditions#")
    }
    Div(Class: panel panel-default){
        Div(Class: panel-body){
            Div(Class: table-responsive){
                Div(Class: ){
                    Table(Source:src_sign,Columns:"Contract name=name,Value=value,Action=edit_sign")
                }
            }
        }
    }
}

Div(Class: row){
    Div(Class: content-wrapper){
		SetTitle(New Signature)
		Div(breadcrumb){
			Div(){
				LinkPage(Signature, Signatures)
				Span(/).Style(margin-right: 10px; margin-left: 10px;)
				Span(Create, text-muted)
			}
		}
        Form(){
            Div(Class: panel panel-default){
                Div(Class: panel-body){
                    Div(Class: col-md-12){
                        Div(Class: form-group){
                            Label(Contract name)
                            Input(Name: "Name", Type: "text",Class:form-control,Placeholder: "MoneyTransfer")
                        }
                        Div(Class: form-group){
                            Label(Title of confirmation window)
                            Input(Name:"Text_att",Type: text,Class: form-control)
                        }
                    }
					
					If(GetVar(count)==""){
						SetVar(count, 0)
						SetVar(next_count, Calculate( Exp: #count# + 1, Type: int))
					}.Else{
						SetVar(next_count, Calculate( Exp: #count_sec# + 1, Type: int))
					}
                    
                    Range(params_range, 0, #next_count#)
                    Div(Class: form-group){
                        Input(Name: next_count, Class: form-control hidden, Type: number, Value: #next#)
                        ForList(Source: params_range){
                            Div(Class: col-md-4){
                                Label(Parameter)
                                Input(Name:"Name_param",Type: text,Class: form-control)
                            }
                            Div(Class: col-md-8){
                                Label(Text)
                                Input(Name:"Text_param",Type: text,Class: form-control)
                            }
                        }
                        
                        Div(Class: col-md-4){
                            Button(Body: Add Param, Class: mt-sm btn btn-primary, Page: Create_Signatures, PageParams:"count_sec=#next_count#,count=#count#")
                        }
                    }
                    
                    Div(Class: form-group){
                        Div(Class: col-md-12){
                            Label(Conditions for change)
                            Input(Name:"Conditions",Type: text,Class: form-control)
                        }
                    }
                }
                Div(Class: panel-footer){
                    Div(Class: clearfix){
                        Div(Class: pull-right){
                            Button(Save,Class: btn btn-primary,Contract:Sign_param,Page:Signatures)
                        }
                    }
                }
            }
        }
    }
}

