contract AppendPage {
		data {
			Id         int
			Value      string
		}
		conditions {
			RowConditions("pages", $Id)
		}
		action {
			var value string
			var row map
			row = DBRow("pages").Columns("value").WhereId($Id)
			value = row["value"]
			if Contains(value, "PageEnd:") {
				value = Replace(value, "PageEnd:", $Value) + "\r\nPageEnd:"
			} else {
				value = value + "\r\n" + $Value
			}
			DBUpdate("pages", $Id, "value",  value )
		}
	}

