contract Companies_Participants_Add {
    data {
        company_id int
        member_id int
        right_sign int
        inclusion_in_role int
        role_id int "optional"
    }

    conditions {
        // check creator of the company
        if ($company_id != $key_id) {
            warning "Sorry, you are not the creator of this company"
        }

        // check member
        $member_map = DBFind("members").Where("id = $", $member_id).Row()
        if ($member_map["id"] == nil) {
            warning "Member not found"
        }

        // check role
        if ($inclusion_in_role == 1) {
            $role_map = DBFind("roles_list").Where("id = $", $role_id).Row()
            if ($role_map["id"] == nil) {
                warning "Role not found"
            }

            if (Int($role_map["company_id"]) != $company_id) {
                warning "This role is not related to the specified company"
            }
        }

        // check - may be added before
        if ($inclusion_in_role == 1) {
            $assign_id = DBFind("companies_participants").Where("company_id=$ and member_id=$ and role_id=$ and delete=$", $company_id, $member_id, $role_id, 0).One("id")
        } else {
            $assign_id = DBFind("companies_participants").Where("company_id=$ and member_id=$ and role_id=$ and delete=$", $company_id, $member_id, 0, 0).One("id")
        }
        if ($assign_id != nil) {
            warning "This member has already been added to the specified role of the company"
        }
    }

    action {
        if ($inclusion_in_role == 0) {
            DBInsert("companies_participants", "company_id,member_id,member_name,member_avatar,right_sign,timestamp date_start,role_id",
                $company_id, $member_id, $member_map["member_name"], $member_map["avatar"], $right_sign, $block_time,0)
        } else {
            DBInsert("companies_participants", "company_id,member_id,member_name,member_avatar,right_sign,timestamp date_start,role_id,role_name",
                $company_id, $member_id, $member_map["member_name"], $member_map["avatar"], $right_sign, $block_time, $role_id, $role_map["role_name"])

            Roles_Assign("role_id,member_id", $role_id, $member_id)
        }
    }
}

contract Roles_Create {
    data {
        role_name string
        role_type int
        company_id int "optional"
    }
    
    conditions {
        // system role
        if ($role_type == 3) {
            warning "System role can not be created"
        }
        
        // company role
        if ($role_type == 4) {
            
            // check param
            if ($company_id == 0) {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }
            
            // check the company
            $company_map = DBFind("companies").Where("id = $", $company_id).Row()
            if ($company_map["id"] == nil) {
                warning "Company not found"
            }
            
            // check creator of the company
            if ($company_id != $key_id) {
                warning "Sorry, you are not the creator of this company"
            }
        }
        
        // check member
        $member_map = DBFind("members").Where("id = $", $key_id).Row()
        if ($member_map["id"] == nil) {
            warning "You are not a member of the ecosystem"
        }
    }
    
    action {
        if ($role_type == 4) {
            $result = DBInsert("roles_list", "role_name,role_type,creator_id,creator_name,creator_avatar,company_id,timestamp date_create,delete",
            $role_name, $role_type, $key_id, $member_map["member_name"], $member_map["avatar"], $company_id, $block_time, 0)
        } else {
            $result = DBInsert("roles_list", "role_name,role_type,creator_id,creator_name,creator_avatar,company_id,timestamp date_create,delete",
            $role_name, $role_type, $key_id, $member_map["member_name"], $member_map["avatar"], 0, $block_time, 0)
        }
    }
}

contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        $assign_map = DBFind("roles_assign").Where("id=$", $row_id).Row()
        if ($assign_map["id"] == nil) {
            warning "Role not found for the specified member"
        }

        // get role
        $role_map = DBFind("roles_list").Where("id=$", Int($assign_map["role_id"])).Row()
        if ($role_map["id"] == nil) {
            warning "Role not found in the roles registry"
        }

        // check creator of the role
        if (Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // delete the member
        DBUpdate("roles_assign", $row_id, "delete,timestamp date_end", 1, $block_time)

        // if company is specified, delete the member from the company
        if (Int($role_map["company_id"]) != 0) {

            // search member in the company
            $assign_id = DBFind("companies_participants").Where("company_id=$ and role_id=$ and member_id=$ and delete=$", Int($role_map["company_id"]), Int($assign_map["role_id"]), Int($assign_map["member_id"]), 0).One("id")

            // if found, then delete the member
            if ($assign_id != nil){
                Companies_Participants_Del("participant_id", Int($assign_id))
            }
        }

    }
}

contract Roles_Del {
    data {
        role_id int
    }

    conditions {
        // check role
        $role_map = DBFind("roles_list").Where("id=$", $role_id).Row()
        if ($role_map["id"] == nil) {
            warning "Role not found"
        }

        // system role can not be deleted
        if (Int($role_map["role_type"]) == 3) {
            warning "System role can not be removed"
        }

        // check creator of the role
        if (Int($role_map["creator_id"]) != $key_id) {
            warning "Sorry, you are not the creator of this role"
        }
    }

    action {
        // get the list of participants
        $ret_assign = DBFind("roles_assign").Columns("id").Where("role_id = $ and delete = $", $role_id, 0).Order("id")

        // delete all participants
        $len_assign = Len($ret_assign)
        $i_assign = 0
        while ($i_assign < $len_assign) {
            $vals_assign = $ret_assign[$i_assign]
            Roles_Unassign("row_id", Int($vals_assign["id"]))
            $i_assign = $i_assign + 1
        }

        // delete the role
        DBUpdate("roles_list", $role_id, "delete,timestamp date_delete", 1, $block_time)
    }
}

contract SystemFunctions {
	}

	func DBFind(table string).Columns(columns string).Where(where string, params ...)
		 .WhereId(id int).Order(order string).Limit(limit int).Offset(offset int).Ecosystem(ecosystem int) array {
		return DBSelect(table, columns, id, order, offset, limit, ecosystem, where, params)
	}

	func One(list array, name string) string {
		if list {
			var row map 
			row = list[0]
			return row[name]
		}
		return nil
	}

	func Row(list array) map {
		var ret map
		if list {
			ret = list[0]
		}
		return ret
	}

	func DBRow(table string).Columns(columns string).Where(where string, params ...)
		.WhereId(id int).Order(order string).Ecosystem(ecosystem int) map {

		var result array
		result = DBFind(table).Columns(columns).Where(where, params ...).WhereId(id).Order(order).Ecosystem(ecosystem)

		var row map
		if Len(result) > 0 {
			row = result[0]
		}

		return row
	}

	func ConditionById(table string, validate bool) {
		var row map
		row = DBRow(table).Columns("conditions").WhereId($Id)
		if !row["conditions"] {
			error Sprintf("Item %d has not been found", $Id)
		}

		Eval(row["conditions"])

		if validate {
			ValidateCondition($Conditions,$ecosystem_id)
		}
	}

