contract sysparams_CandidateValidatorRequest{
    data{
        HostIp string
        // CommissionPub string
        CommissionAddress string
        Description string "optional"
        Comment string "optional"
    }
    
    func IsNotValidIp(ip string)bool{
        var parts array
        var i, lenParts, part int
        parts = Split(ip, ".")
        
        lenParts = Len(parts)
        if lenParts != 4{
            return true
        }
        while i< lenParts {
            part = Int(parts[i])
            if part < 0{
                return true
            }
            if part > 255{
                return true
            }
            i = i+1
        }
        
        return false
    }
    
    conditions{
        // $CommissionPub = Replace($CommissionPub, " ", "")
        // info Sprintf("%v %v", $CommissionAddress, PubToID($CommissionPub))
        // if $CommissionAddress != PubToID($CommissionPub) {
        // info "Address or Public key are invalid"
        // }
        
        $CommissionAddress = AddressToId($CommissionAddress)
        if !DBFind("keys").Where("id=?", $CommissionAddress).Row(){
            info "Address not valid"
        }
        
        if IsNotValidIp($HostIp){
            info "Please enter a valid ip address"
        }
        
        sysparams_RoleAssign("RoleName,KeyId", "Candidate for validators", $key_id)
    }
    
    action{
        
        DBInsert("sysparams_candidate", "host_ip,commission_pub,commission_address,description,comment,candidate_key_id,timestamp date_created,deleted", $HostIp, "", $CommissionAddress, $Description, $Comment, $key_id, $block_time, 0)
        
    }
    
}

contract voting_SubjectCheckFulldata {
    data {
        votingID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=?", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting is not found"
		}	

		$subject_map = DBFind("voting_subject").Where("voting_id=?", $votingID).Row()
		if ($subject_map["id"] == nil){
			warning "Subject of voting is not found"
		}

		$typedecision = Int($voting_map["typedecision"])
    }

    action {
		if ( ($typedecision == 1) || ($typedecision == 2) ){
			if ( (Int($voting_map["optional_number_cands"]) > 0) && (Int($voting_map["optional_role_vacancies"]) > 0) && (Int($voting_map["optional_role_id"])  > 0) ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 3){
			if ( (string($subject_map["text_document"]) != "") && (string($subject_map["text_doc_hash"]) != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 4){
			if ( (string($subject_map["formal_decision_description"]) != "") && (string($subject_map["formal_decision_table"])  != "") && 
				 (string($subject_map["formal_decision_tableid"])     != "") && (string($subject_map["formal_decision_column"]) != "") && 
				 (string($subject_map["formal_decision_colvalue"])    != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}

		if ($typedecision == 5){
			if ( (string($subject_map["contract_accept"]) != "") && (string($subject_map["contract_reject"]) != "") ){
				DBUpdate("voting_instances", $votingID, "flag_fulldata", 1)
			} 
		}
    }
}

contract sysparams_RefreshInvestors {
    data {
        role_id int
    }

    conditions{
        $limitInvestors = 3

        $founder = EcosysParam("founder_account")
        if Str($key_id) != $founder{
            info "You can not start to voting"
        }

        $role = DBFind("roles_list").Where("id=?", $role_id).Row()
        if !$role{
            warning "Role not found"
        }

        $role_name = $role["role_name"]
        $role_type = Int($role["role_type"])

    }

    action{
        // remove old investors
        DBUpdateExt("roles_assign", "role_id", $role_id, "delete", 1)

        // get appointer
        $appointerFind = DBFind("members").Where("member_id=?",$key_id)
        $appointer = $appointerFind[0]

        // assign new investors
        $investorsFind = DBFind("keys").Order("amount desc, id").Limit($limitInvestors)
        $lenInvestors = Len($investorsFind)

        $i = 0
        while($i < $lenInvestors){
            $investor = $investorsFind[$i]
            $memberFind = DBFind("members").Where("member_id=?", $investor["id"])
            if Len($memberFind) > 0{
                $member = $memberFind[0]
                DBInsert("roles_assign", "role_id,role_type,role_name,member_id,member_name,member_avatar,appointed_by_id,appointed_by_name,timestamp date_start,delete", $role_id, $role_type, $role_name, $investor["id"], $member["member_name"], $member["avatar"], $key_id, $appointer["member_name"], $block_time, 0)
            }
            $i =$i+1
        }
    }
}

contract sysparams_DelegateRefresh{
    func NotInArray(val string, arr array) bool{
        var i int
        // info arr
        while i< Len(arr){
            if val == arr[i]{
                return false
            }
            i=i+1
        }
        return true
    }
    
    func conditions{
        $roleName = "Delegate"
        $delegateParam = "delegate_account"
        
        // get role Id and Type
        $role = DBFind("roles_list").Where("role_name=?", $roleName).Order("id").Row()
        if !$role{
            return "Role not found"
        }
    }
    
    func action{
        // get delegates ids
        var delegateIds array
        var dIndex int
        $i = 1
        while $i <= $ecosystem_id {
            $delegate = DBFind("parameters").Where("name=?",$delegateParam).Ecosystem($i).Row()
            if !$delegate{
                $delegate = DBFind("parameters").Where("name='founder_account'").Ecosystem($i).Row()
            }
            $delegateValue = $delegate["value"]
            if NotInArray($delegateValue, delegateIds){
                delegateIds[dIndex] = $delegateValue
                dIndex = dIndex + 1
            }
            $i = $i + 1
        }
        
        // remove old role assigns
        $currentDelegates = DBFind("roles_assign").Where("role_id=? and delete=0", $role["id"])
        $i = 0
        while $i < Len($currentDelegates){
            $currentDelegate = $currentDelegates[$i]
            if NotInArray($currentDelegate["member_id"], delegateIds){
                $Id = $currentDelegate["id"]
                DBUpdate("roles_assign", Int($Id), "delete", 1)
            }
            $i = $i + 1
        }
        
        // assign new roles
        var i int
        while i < Len(delegateIds) {
            var delegateId int
            delegateId = Int(delegateIds[i])
            var member map
            member = DBFind("members").Where("id=?",delegateId).Row()
            if member{
                // check role dup
                var assigned map
                assigned = DBFind("roles_assign").Where("member_id=? and role_id=? and delete=0", delegateId, $role["id"]).Row()
                if !assigned{
                    sysparams_RoleAssign("RoleName,KeyId", $roleName, Int(member["id"]))
                }
            }
            i=i+1
        }
    }
}

contract sysparams_AcceptRole{
