contract sysparams_NewVoting {
    data {
        v_name string
        new_val string

        now_date string "date"
        start_date string "date"
        end_date string "date"
    }

    conditions {
        $voting_name = "New system parameter"
        $description = "Voting for the system parameter"
        $volume = 75
        $quorum = 50

        $role_name = "Investor with voting rights"
        $type_participants = 3 // role
        $type_decision = 4 // formal decision
        $table_to_written = "system_parameters"
        $column_name = "value"
    }

    action {
        // create new voting
        voting_CreateNew("voting_name,type_voting,description,type_participants,type_decision,now_date,start_time,start_date,end_date,end_time,volume,quorum",
            $voting_name, 2, $description, $type_participants, $type_decision, $now_date, "00:00", $start_date, $end_date, "00:00", $volume, $quorum)

            // get voting id
            $ret_instance = DBFind("voting_instances").Order("id DESC")
            if(Len($ret_instance) > 0){
                $vals_instance = $ret_instance[0]
                $votingID = Int($vals_instance["id"])

                // set subject of voting
                voting_SubjectFormal("votingID,description,table_to_written,row_id,column_name,written_value",
                    $votingID, $description, "system_parameters", 0, $v_name, $new_val)

                    // search role
                    $ret_role = DBFind("roles_list").Where("role_name=$ and role_type=$ and delete=0", $role_name, 3).Order("id")
                    if(Len($ret_role) > 0){
                        $vals_role = $ret_role[0]
                        $role_id = Int($vals_role["id"])

                        // refresh list of the investors in the role
                        sysparams_RefreshInvestors("role_id", $role_id)

                        // add all participants from the role
                        voting_Invite("votingID,var_id", $votingID, $role_id)
                    }
                }
            }
        }

contract voting_TemplateRun{
    data{
        KeyId int "optional"
        TemplateId int
        Start string "date"
        StartTime string "date,optional"
        End string "date"
        EndTime string "date,optional"
    }
    
    func conditions{
        $template = DBFind("voting_templates").WhereId($TemplateId).Row()
        if !$template{
            warning "Voting template not found"
        }
        if $KeyId != 0{
            $keyFind = DBFind("keys").Where("id=?", $KeyId).Row()
            if !$keyFind{
                warning "Keyid not found"
            }
        }
        if $StartTime == ""{
            $StartTime = "00:00"
        }
        if $EndTime == ""{
            $EndTime = "23:59"
        }
        
        $condition = $template["init_condition"]
        ContractConditions($condition)
        
        $candidateRoleName = $template["precondition"]
        $votersRoleName = $template["voters"]
        $votingName = $template["title"]
        $volume = Int($template["volume"])
        $quorum = Int($template["quorum"])
        $typeVoting = Int($template["type_voting"])
        $typeParticipants = Int($template["type_participants"])
        $typeDecision = Int($template["type_decision"])
        $vacancies = Int($template["vacancies"])
        
        $desc = Sprintf("%v [#%v]", $votingName, $TemplateId)

        $contractAccept = $template["contract_accept"]
        $acceptParams = $template["contract_accept_params"]
        $contractReject = $template["contract_reject"]
        $rejectParams = $template["contract_reject_params"]
        
        $isCreated = DBFind("voting_instances").Where("name=? and description=? and delete=0", $votingName, $desc).Row()
        if $isCreated{
            // info "Voting already created" // must uncomment on prod
        }
        
        // if not special then use default
        $defaultParams = Sprintf("VotingTemplateId->%v;KeyId->%v", $TemplateId, $KeyId)
        if Size($acceptParams) == 0{
            $acceptParams = $defaultParams
        }
        if Size($rejectParams) == 0{
            $rejectParams = $defaultParams
        }
    }
    
    func action{
        var roleId int
        roleId = DBFind("roles_list").Where("role_name=? and delete=0", $votersRoleName).One("id")
        
        $voting_id = 0 // will be set in voting_CreateNew
        voting_CreateNew("voting_name,type_voting,description,type_participants,type_decision,now_date,start_time,start_date,end_date,end_time,volume,quorum", $votingName, $typeVoting, $desc, $typeParticipants, $typeDecision, $Start, $StartTime, $Start, $End, $EndTime, $volume, $quorum)
        if $voting_id == 0 {
            $voting_id = DBFind("voting_instances").Where("voting_name=? and start_date=?", $votingName, $Start).One("id")
            $voting_id = Int($voting_id)
        }
        // after success voting set Validator Role to Candidate
        voting_SubjectCandidates("votingID,memberID", $voting_id, $KeyId)
        voting_SubjectSettings("votingID,roleID,vacancies,contract_reject,contract_accept,accept_params,reject_params", $voting_id, roleId, $vacancies, "", $contractAccept, $acceptParams, $rejectParams)
        $value = Sprintf("%v : %v", $voting_id, roleId)
        
        voting_Invite("votingID,var_id", $voting_id, roleId)
        
    }
}

contract sysparams_StartVotingValidator{
    data{
        Id int
        VotingParamId int
        Start string "date"
        StartTime string "date"
        End string "date"
        EndTime string "date"
    }
    
    func conditions{
        $votingParams = DBFind("voting_templates").WhereId($VotingParamId).Row()
        if !$votingParams{
            warning "Voting params not found"
        }
        
        $condition = $votingParams["init_condition"]
        ContractConditions($condition)
        
        // Delegate to Validators voting :    
        if $votingParams["title"] != "Voting for Validator"{
            warning "Invalid param"
        }
        // renew Delegates
        $candidate = DBFind("sysparams_candidate").Where("id=?",$Id).Row()
        if !$candidate{
            warning "Candidate not found"
        }
        $KeyId = Int($candidate["candidate_key_id"])
        
        sysparams_DelegateRefresh()
    }
    
    func action{
        voting_TemplateRun("KeyId,TemplateId,Start,StartTime,End,EndTime",$KeyId,$VotingParamId,$Start,$StartTime,$End,$EndTime)
    }
}

contract voting_TemplateStore{
    data{
        Title string
        TypeParticipants int
        TypeVoting int
        TypeDecision int
        Volume int
        Quorum int
        Precondition string "optional" //old subject, any propertion
        Subject string
        Voters string
        InitCondition string "optional"
        Vacancies int
        ContractAccept string "optional"
        ParamsAccept string "optional"
        ContractReject string "optional"
        ParamsReject string "optional"
    }

    func conditions{
        if Size($InitCondition) > 0{
            if !IsObject($InitCondition, $ecosystem_id){
                warning "Init condition not found"
            }
        }

        if Size($Title) <3{
            warning "Please enter title"
        }
        if $Volume <0 || $Volume > 100{
            warning "Wrong volume range"
        }
        if $Quorum <0 || $Quorum > 100{
            warning "Wrong volume range"
        }

        if $Vacancies < 1 {
            warning "wrong number vacancies"
        }

        if Size($ContractAccept) > 0{
            if !IsObject($ContractAccept, $ecosystem_id){
                warning "wrong contract on voting accept"
            }
        }
        if Size($ContractReject) > 0{
            if !IsObject($ContractReject, $ecosystem_id){
                warning "wrong contract on voting accept"
            }
        }
    }

    func action{
        DBInsert("voting_templates", "title,precondition,subject,voters,init_condition,volume,quorum,type_voting,type_participants,type_decision,vacancies,contract_accept,contract_accept_params,contract_reject,contract_reject_params", $Title,$Precondition,$Subject,$Voters,$InitCondition,$Volume,$Quorum,$TypeVoting,$TypeParticipants,$TypeDecision,$Vacancies,$ContractAccept,$ParamsAccept,$ContractReject,$ParamsReject)

    }
}

contract voting_RejectDecision {
    data {
        votingID int
    }

    conditions {
		$voting_map = DBFind("voting_instances").Where("id=$", $votingID).Row()
		if ($voting_map["id"] == nil){
			warning "Voting is not found"
		}

		// check the participant of the voting
		$participant_id = DBFind("voting_participants").Where("voting_id=$ and member_id=$", $votingID, $key_id).One("id")
		if ($participant_id == nil){
			warning "You are not a participant in this voting"
		}
    }

    action {
		// if notifications have been sent, then close it
		if ( Int($voting_map["flag_notifics"]) == 1 ){
			var params string
			params = "notific_param=" + Str($votingID)	
			$notific_id = DBFind("notifications").Where("recipient_id=$ and page_name=$ and page_params=$ and closed=$", $key_id, "voting_view", params, 0).One("id")
			if ($notific_id != nil){
				Notifications_Single_Close("notific_id", Int($notific_id))
			}
		}

		// check the decision
		if ( Int($voting_map["flag_decision"]) == 1 ){
			warning "Decision was taken. Voting is now not possible"
		} 

		// check start-end of voting time
		$voting_start = DBFind("voting_instances").Where("id=$ and startdate < now()", $votingID).One("id")
		$voting_end   = DBFind("voting_instances").Where("id=$ and enddate   > now()", $votingID).One("id")
		if ($voting_start == nil){
			warning "Voting has not yet begun. Try again later, please"
		}
		if ($voting_end == nil){
			warning "Voting has expired. Voting is now not possible"
		}

		//-----------------------------------------------------------------------------------------------

		// write the result of the voting
		DBUpdate("voting_participants", Int($participant_id), "timestamp decision_date, decision", $block_time, -1)

		voting_UpdateDataForGraphs("votingID", $votingID)
    }
}

