        If(#delete# == 0){
            Span(Class: text-normal, Body: EcosysParam(Name: company_type, Index: #company_type#))
        }.Else{
            Span(Class: text-muted, Body: EcosysParam(Name: company_type, Index: #company_type#))
        }
