contract cc_nextStep {

    data {
        application_id int
        message string "optional"
        info_message string "optional"
    }

    conditions {
        var rejected int
        rejected = DBFind("cc_applications").Where("id = ?", $application_id).One("rejected_finally")
        if (rejected > 0) {
            warning "Application rejected"
        }
    }

    action {

        var confirmatons map
        confirmatons = DBFind("cc_confirmations").Where("application_id = ?", $application_id).Row()

        while (true) {

            if (confirmatons["step_3"] == 0) {
                var admin_id int
                admin_id = DBFind("roles_list").Where("role_name = ?", "Admin").One("id")

                DBUpdate("cc_applications", $application_id, "current_step", "3")

                var params string
                params = Sprintf("application_id=%v,stage=%v,stage_str=%v,stage_name=%v,role_name=%v,message=%v,info_message=%v", $application_id, "3", "3", "Initial review", "Admin", $message, $info_message)

                Notifications_Roles_Send("role_id,closure_type,icon_name,text_header,text_body,page_name,params_val", admin_id, 1, "fa-file-alt", "Cabin Crew Application", "Initial review", "cc_initialReview", params)
                Notifications_Roles_Processing("notific_id", $notification_id)
                break
            }
            if (confirmatons["step_4"] == 0) {
                var inspector_id int
                inspector_id = DBFind("roles_list").Where("role_name = ?", "Inspector").One("id")

                DBUpdate("cc_applications", $application_id, "current_step", "4")

                var params string
                params = Sprintf("application_id=%v,stage=%v,stage_str=%v,stage_name=%v,role_name=%v,message=%v,info_message=%v", $application_id, "4", "4", "Final review", "Inspector", $message, $info_message)

                Notifications_Roles_Send("role_id,closure_type,icon_name,text_header,text_body,page_name,params_val", inspector_id, 1, "fa-file-alt", "Cabin Crew Application", "Final review", "cc_finalReview", params)
                Notifications_Roles_Processing("notific_id", $notification_id)
                break
            }

            break
        }


    }
}

contract cc_application_del {
    data {
        application_id int
    }

    conditions {
    }

    action {
        DBUpdate("cc_applications", $application_id, "delete,timestamp date_delete", 1, $block_time)
    }
}

contract cc_approve {
    data {
        notific_id int
        application_id int
        message string
    }

    conditions {

    }

    action {
        Notifications_Roles_Finishing("notific_id", $notific_id)

        var date_expiry int
        date_expiry = $block_time + 504576000

        DBUpdate("cc_applications", $application_id, "success,timestamp date_expiry", 1, date_expiry)
        var applicant_id int
        applicant_id = DBFind("cc_applications").Where("id = ?", $application_id).One("member_id")

        var params string
        params = Sprintf("application_id=%v,stage=%v,stage_str=%v,stage_name=%v,role_name=%v,message=%v", $application_id, "41", "4.01", "Application accepted", "Applicant", $message)

        Notifications_Single_Send("member_id,icon_name,text_header,text_body,page_name,params_val", applicant_id, "fa-file-alt", "Cabin Crew Application", "Application accepted", "cc_approved", params) 
    }
}

contract cc_acceptApplication {
    data {
        application_id int
        stage int
        notific_id int
        message string "optional"
    }

    conditions {
    }

    action {
        var step_column_name string
        step_column_name = Sprintf("step_%v", $stage)
        DBUpdateExt("cc_confirmations", "application_id", $application_id, step_column_name, 1)

        DBUpdate("cc_applications", $application_id, "admin_message", $message)

        Notifications_Roles_Finishing("notific_id", $notific_id)

        cc_nextStep("application_id,message", $application_id, $message)
    }
}

