        member_address string
    }

    conditions {

    }

    action {
        var member map
        member = DBFind("members").Where("id = ?", $key_id).Row()

        var application_id int
        application_id = DBInsert("cc_applications", "photo,member_name,member_id,timestamp date_issue,date_of_birth,nationality,authority,ratings,remarks,address", member["avatar"], member["member_name"], $key_id, $block_time, $date_of_birth, $nationality, $authority, $ratings, $remarks, $member_address)

