contract MultiwalletCreate {
    data {
        BufferId int
    }
    conditions {
        var buf map
        buf["key"] = "multiaccount_units"
        buf["member_id"] = $key_id
        $buf = DBFind("buffer_data").WhereId($BufferId).Row()
        if !$buf {
            warning "not assigned group"
        }
    }

    action {
        // При клике на кнопке Create
        // в таблице 1_keys в колонку multi записывается
        // id шаблона голосования
        // -1, если выбрано "перевод одной подписью"
        // пользователи и роли КМП заносятся в таблицу доверенностей с указанными лимитами
    }
}

contract MultiwalletUnits {
    data {
        Action string
        Units array
        Vals array
        Type int
    }
    conditions {
        var buf map
        buf["key"] = "multiaccount_units"
        buf["member_id"] = $key_id
        $buf = DBFind("buffer_data").Where(buf).Row()
        if !$buf {
            buf["value"] = `{}`
            buf["id"] = DBInsert("buffer_data", buf)
            $buf = buf
        }else{
            buf["value"] = `{}`
            DBUpdate("buffer_data", Int($buf["id"]), buf)
        }
        var units array i lenVals int
        lenVals = Len($Vals)
        while i < lenVals{
            if $Vals[i] == "true"{
                units = Append(units, $Units[i])
            }
            i = i + 1
        }
        $units = units
    }

    action {

        var id int m map
        id = Int($buf["id"])
        if $Action == "set" {
            m["value->type"] = $Type
            m["value->units"] = JSONEncode($units)
            DBUpdate("buffer_data", id, m)
        }
    }
}
