contract TsTransferAdmin {
    data {
        WalletStr string
        Amount string
        CurrencyType int
        AmountApl money
        TransferredDate string
        TransferredTime string
    }
    func getRoleId(name string) int{
        var rid int
        rid = Int(EcosysParam(name))
        if rid == 0{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }
    func trimZeroTime(s string) string {
        if Contains(s, "T00:00:00Z") {
            s = s Replace(s, "T00:00:00Z", "")
        }
        return s
    }
    func dateAddTime(d, t string) string {
        var dt string
        if Size(t) == 5 {
            dt = Sprintf("%v %v:00", trimZeroTime(d), t)
        }
        return dt
    }
    conditions{
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4
        if $AmountApl <= 0{
            warning "invalid amount APL"
        }
        if $Amount <= 0.0{
            warning "invalid amount"
        }

        $transferredAt = UnixDateTime(dateAddTime($TransferredDate, $TransferredTime))
        if $transferredAt == 0 {
            warning "invalid transfer datetime"
        }
        $appId = Int(DBFind("@1applications").Where({ecosystem:$ecosystem_id, name:"Tokens sale"}).One("id"))
        if $appId == 0{
            warning LangRes("@1app_not_found", "en")
        }
        $roleAplManager = getRoleId("role_sale_manager")
        $currentRole = getRoleId("role_sale_admin")
        if !RoleAccess($currentRole){
            warning "this action allowed only for role id #" + $currentRole
        }

        $WalletId = AddressToId($WalletStr)
        $wallet = DBFind("ts_wallets").Where({key_id_buyer:$WalletId}).Row()
        if !$wallet{
            warning "Buyer wallet not found"
        }
        if $CurrencyType <= 0 || $CurrencyType > 3{
            warning "Invalid Currency Type"
        }
    }

    action {
        var sale map saleId int
        sale["key_id_buyer"] = $wallet["key_id_buyer"]
        sale["currency_type"] = $CurrencyType
        sale["amount"] = $Amount
        sale["amount_apl"] = $AmountApl
        sale["key_id_sale_apl_admin"] = $key_id
        sale["created_at"] = $time
        sale["buyer_transferred_at"] = $transferredAt
        sale["status"] = $WAITING_MANAGER

        saleId = DBInsert("ts_sales", sale)

        // role Apla sales manager notification
        var params map title page string
        title = Sprintf("Check sale id %v", saleId)
        page = "ts_transfer_manager"
        params["sale_id"] = saleId
        @1NotificationsSend("rid,sender,text_header,page_name,params_map,closure_type,current_role_id", $roleAplManager, 2, title, page, params, 1, $currentRole)

    }
}

contract TsTransferManager {
    data {
        SaleId int
        NotificId int
    }
    func getRoleId(name string) int{
        var rid int
        rid = Int(EcosysParam(name))
        if rid == 0{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }
    conditions{
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4
        $appId = Int(DBFind("@1applications").Where({ecosystem:$ecosystem_id, name:"Tokens sale"}).One("id"))
        if $appId == 0{
            warning LangRes("@1app_not_found", "en")
        }
        $roleAplSeller = getRoleId("role_sale_seller")
        $currentRole = getRoleId("role_sale_manager")
        if !RoleAccess($currentRole){
            warning "this action allowed only for role id #" + $currentRole
        }

        $sale = DBFind("ts_sales").Where({id:$SaleId}).Row()
        if !$sale{
            warning "Sale not found"
        }
    }

    action {
        @1NotificationsClose("notific_id", $NotificId)
        DBUpdate("ts_sales", Int($sale["id"]), {key_id_sale_apl_manager:$key_id, status:$WAITING_SELLER})

        // role Apla seller notification
        var params map title page string
        title = Sprintf("Transfer sale id %v", $sale["id"])
        params["sale_id"] = $sale["id"]
        page = "ts_transfer_seller"
        @1NotificationsSend("rid,sender,text_header,page_name,params_map,closure_type,current_role_id", $roleAplSeller, 2, title, page, params, 1, $currentRole)

    }
}

contract TsTransferReject {
    data {
        Comment string
        RoleParam string
        NotificId int
    }
    func getRoleId(name string) int{
        var rid int
        rid = Int(EcosysParam(name))
        if rid == 0{
            warning Sprintf(LangRes("@1role_not_set_application_parameter", "en"), name)
        }
        return rid
    }
    conditions{
        $WAITING_MANAGER = 1
        $WAITING_SELLER = 2
        $FINISHED = 3
        $REJECTED = 4

        if !($RoleParam == "role_sale_manager" || $RoleParam == "role_sale_seller"){
            warning "invalid role parameter"
        }
        var role noteSaleId int note map
        role = getRoleId($RoleParam)
        if !RoleAccess(role){
            warning "this action allowed only for role id #" + role
        }
        note = DBFind("@1notifications").Where({id:$NotificId}).Columns("page_params->sale_id,date_start_processing,notification->type").Row()
        if !note{
            warning "Notification not found"
        }
        $needProcessing = note["date_start_processing"] == ""
        noteSaleId = Int(note["page_params.sale_id"])
        $sale = DBFind("ts_sales").Where({id:noteSaleId}).Row()
        if !$sale {
            warning "Sale not found"
        }
        $saleId = Int($sale["id"])
    }

    action {
        if $needProcessing {
            @1NotificationsProcess("notific_id", $NotificId)
        }
        @1NotificationsClose("notific_id", $NotificId)
        var sale params map title body string prevKeyId int
        title = Sprintf("Sale %v rejected", $saleId)
        body = $Comment
        params["sale_id"] = $saleId
        sale["status"] = $REJECTED
        if $RoleParam == "role_sale_manager"{
            sale["key_id_sale_apl_manager"] = $key_id
            sale["manager_reject_comment"] = $Comment
            sale["manager_rejected_at"] = $time
            prevKeyId = Int($sale["key_id_sale_apl_admin"])

        }elif $RoleParam == "role_sale_seller"{
            sale["key_id_seller"] = $key_id
            sale["seller_reject_comment"] = $Comment
            sale["seller_rejected_at"] = $time
            prevKeyId = Int($sale["key_id_sale_apl_manager"])
        }

        DBUpdate("ts_sales", $saleId, sale)
        @1NotificationsSend("member_id,sender,text_header,text_body,page_name,params_map,closure_type", prevKeyId, 1, title, body, "ts_notification", params, 1)

    }
}

