contract PoaCondition {
    data {
        Name string
        Wallet int
    }

    conditions {
        var attorney map
        attorney = DBFind("power_of_attorney").Where({deleted:0, "contract":$Name, "address":$Wallet, key_id_attorney:$key_id}).Row()

        if !attorney{
            // feature for role attorney
            // attorney = DBFind("power_of_attorney").Where({deleted:0, "contract":$Name, "address":$Wallet, rid:$role_id}).Row()
            if !attorney{
                warning Sprintf("You don't have access to wallet %s", $Wallet)
            }
        }
        if attorney["date_expiration"] != 0{
            if attorney["date_expiration"] < $time{
                warning "The power of attorney expired"
            }
        }
        $result = attorney
    }
}

contract PoaDelete {
    data {
        Id int
    }
    conditions {
        $attorney = DBFind("power_of_attorney").Where({deleted:0, id:$Id}).Row()
        if !$attorney {
            warning "Power of attorney does not exists"
        }
        if !($attorney["address"] == $key_id || $attorney["key_id_attorney"] == $key_id) {
            warning "Deleting Power of attorney is impossible"
        }
    }
    action {
        DBUpdate("power_of_attorney", $Id, {deleted:$time})
    }
}

contract PoaParamDelete {
    data {
        Id int
        Param string
    }
    conditions {
        $attorney = DBFind("power_of_attorney").Where({id:$Id, "address":$key_id, deleted:0}).Row()
        if !$attorney {
            warning "Power of attorney does not exists"
        }
    }
    action {
        var params newParams map keys array i lenKeys int key string
        params = JSONDecode($attorney["params"])
        keys = GetMapKeys(params)
        lenKeys = Len(keys)
        while i < lenKeys{
            key = keys[i]
            if key != $Param{
                newParams[key] = params[key]
            }
            i = i + 1
        }
        DBUpdate("power_of_attorney", $Id, {params:"{}"})
        DBUpdate("power_of_attorney", $Id, {params:newParams})
    }
}

contract PoaStore {
    data {
        Id string "optional"
        MemberId int
        NameContract string "optional" // manual catch
        ExpireAt string "optional"
        Params array "optional"
        Values array "optional"
    }
    func dateAddTime(d string) string {
        if Size(d) == 10{
            return d + " 00:00:00"
        }
        return d
    }
    conditions {
        $Id = Int($Id)
        if $Id > 0 {
            if !DBFind("power_of_attorney").Where({id:$Id, "address":$key_id, deleted:0}).One("id") {
                warning "Power of attorney does not exists"
            }
        }
        if !DBFind("keys").WhereId($MemberId).One("id") {
            warning "Attorney not found"
        }
        Println("NameContract",$NameContract)
        Println("ExpireAt",$ExpireAt)
        $ExpireAt = dateAddTime($ExpireAt)
        if GetContractByName($NameContract) == 0{
            warning "Contract for power of attorney not found"
        }
        if Len($Params) > Len($Values) {
            error "Incorrect params list"
        }
    }
    action {
        // amount_max - максимальная сумма для одноразового перевода
        // amount_max_day - максимальная сумма для перевода в день
        // amount_day - переведено в текущий день
        // date_day - учетный день
        // amount_max_month - максимальная сумма для перевода в месяц
        // amount_month переведено в текущий месяц
        // date_month - учетный месяц

        var params map i id lenKeys int
        lenKeys = Len($Params)
        while i < lenKeys {
            params[$Params[i]] = $Values[i]
            i = i + 1
        }
        var m map
        m["key_id_attorney"] = $MemberId
        m["contract"] = $NameContract
        m["date_expiration"] = UnixDateTime($ExpireAt)
        m["params"] = JSONEncode(params)
        if $Id > 0 {
            DBUpdate("power_of_attorney", $Id, m)
        }else{
            m["address"] = $key_id
            m["deleted"] = 0
            id = DBInsert("power_of_attorney", m)

            var n map
            n["member_id"] = $MemberId
            n["sender"] = 1
            n["text_header"] = "You got new power of attorney"
            n["page_name"] = "poa_view"
            n["params_map"] = Sprintf(`{"id":"%v"}`, id)
            CallContract("NotificationsSend", n)
        }
    }
}

contract PoaTokensTransferWrapper {
    data {
        Amount money
        AttorneyId int
        Recipient_AccountId int
        Comment string "optional"
    }
    func convertToMoney(n int) money{
        var i int res dec money
        res = Money(Str(n))
        dec = Money("10")
        while i < $money_digit {
            res = res * dec
            i = i + 1
        }
        return res
    }

    func checkLimit(num money, lim int, s string){
        if num > convertToMoney(lim) {
            warning Sprintf("Maximum %v transfer limit exceeded (%v APL)", s, lim)
        }
    }
    func safeMoney(n ...) money{
        var res money
        if n[0]{
            res = Money(Str(n[0]))
        }
        return res
    }
    func checkDateAmount(m map){
        var curYear curMonth curDay int bt string zero money
        bt = BlockTime()
        curYear = Int(Substr(bt, 0, 4))
        curMonth = Int(Substr(bt, 5, 2))
        curDay = Int(Substr(bt, 8, 2))
        if m["date_year"] != curYear {
            m["amount_month"] = zero
            m["amount_day"] = zero
        }
        if m["date_month"] != curMonth {
            m["amount_month"] = zero
            m["amount_day"] = zero
        }
        if m["date_day"] != curDay {
            m["amount_day"] = zero
        }
        m["date_year"] = curYear
        m["date_month"] = curMonth
        m["date_day"] = curDay
    }
    conditions {
        var attorney ap map
        attorney = DBFind("power_of_attorney").Where({id:$AttorneyId, deleted:0, "contract":"TokensTransfer", key_id_attorney:$key_id}).Row()

        if !attorney{
            // feature for role attorney
            // attorney = DBFind("power_of_attorney").Where({deleted:0, "contract":$Name, "address":$Wallet, rid:$role_id}).Row()
            if !attorney{
                warning Sprintf("You don't have access to wallet %s", $Wallet)
            }
        }
        if attorney["date_expiration"] != 0{
            if attorney["date_expiration"] < $time{
                warning "The power of attorney expired"
            }
        }
        $Sender_AccountId = Int(attorney["address"])

        $money_digit = Int(EcosysParam("money_digit"))

        ap = JSONDecode(attorney["params"])

        ap["amount_max"] = Int(ap["amount_max"])
        ap["amount_max_day"] = Int(ap["amount_max_day"])
        ap["amount_max_month"] = Int(ap["amount_max_month"])
        ap["amount_day"] = safeMoney(ap["amount_day"])
        ap["amount_month"] = safeMoney(ap["amount_month"])

        checkDateAmount(ap)
        ap["amount_day"] = ap["amount_day"] + $Amount
        ap["amount_month"] = ap["amount_month"] + $Amount

        checkLimit($Amount, ap["amount_max"], "")
        checkLimit(ap["amount_day"], ap["amount_max_day"], "day")
        checkLimit(ap["amount_month"], ap["amount_max_month"], "month")


        $params = JSONEncode(ap)
        $id = Int(attorney["id"])
    }

    action {
        TokensTransfer("Amount,Sender_AccountId,Recipient_AccountId,Comment", $Amount, $Sender_AccountId, $Recipient_AccountId, $Comment)
        DBUpdate("power_of_attorney", $id, {params:$params, date_last_transaction:$time})
    }
}
