Div(content-wrapper){
    DBFind(Name:applications, Source:src_app).Columns("name,id").Where("name='Basic application'").Vars(app)
    SetTitle($roles$)
    AddToolButton(Title: $create$, Page: roles_create, Icon: icon-plus)

    If(GetVar(search)){
        SetVar(where,"role_name='#search#' and deleted=0")
    }.Else{
        SetVar(where,"id>0 and deleted=0").(search,)
    }

    DBFind(Name: roles, Source: src_roles).Custom(_id){
        JsonToSource(rids,#roles_access#)
        If(#deleted# == 0){
            SetVar(style_text,).(style_link,"text-primary")
        }.Else{
            SetVar(style_text,"text-muted").(style_link,"text-muted")
        }
        Span(Class: #style_text#, Body: #id#)
    }.Custom(_creator){
        If(#role_type#==3){
        }.ElseIf(And(#creator.member_name#!=NULL,#creator.member_name#!="")){
            LinkPage(Class: #style_link# text-bold, Page: profile_view, PageParams: "v_member_id=#creator.member_id#"){
                #creator.member_name#
            }
        }
    }.Custom(_name){
        LinkPage(Class: #style_link# h5 text-bold, Page: roles_view, PageParams: "v_role_id=#id#"){
            If(#image_id#>0){
                Image(Src: Binary().ById(#image_id#), Class: mr-sm).Style(width: 30px; border: 1px solid #5A5D63;)
                #role_name#
            }.Else{
                Div(){
                    Span(Class: fa icon-settings fa-2x mr-sm).(#role_name#)
                }.Style(display:flex; align-items:center;)
            }
        }
    }.Custom(_date){
        Div(#style_text# small){
            Div(text-nowrap){DateTime(DateTime: #date_created#, Format: "DD.MM.YYYY HH:MI")}
            Div(text-nowrap){DateTime(DateTime: #date_deleted#, Format: "DD.MM.YYYY HH:MI")}
        }
    }.Custom(_type){
        Span(Class: #style_text#, Body: AppParam(App:#app_id#, Name: roles_types, Index: #role_type#))
    }.Custom(_status){
        Span(Class:#style_text#){
            If(#deleted# == 0){
                LangRes(active)
            }.Else{
                LangRes(deleted)
            }
        }
    }.Custom(_access){
        If(#creator.member_id#==#key_id#){
            SetVar(access,done)
        }.Else{
            SetVar(access,)
        }
        If(#roles_access.rids#!=""){
            DBFind(roles, src_roles_list).Where("id in (#roles_access.rids#)").Columns("id,role_name").Count(access_count).Order(role_name)
            Div(#style_text# breaker){
                ForList(src_roles_list){
                    If(And(#role_id#==#id#)){
                        SetVar(access,done)
                    }

                    If(#access_count#!=#src_roles_list_index#){
                        Span("#role_name#,",mr-sm)
                    }.Else{
                        #role_name#
                    }
                }
            }
        }
    }.Custom(_actions){
        Div(text-right button-group text-nowrap){
            If(#access#=="done"){
                Button(Class: fa fa-edit btn btn-default, PageParams: "rid=#id#", Page: roles_create)
                If(And(#deleted#==0,#role_type#!=3)){
                    Button(Class: fa fa-trash btn btn-danger, Contract: Roles_Del, Params: "rid=#id#", Page: roles_list).Alert(Text: $sure_want_delete$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                }.ElseIf(#deleted#==1){
                    Button(Class: fa fa-recycle btn btn-info, Contract: Roles_Restore, Params: "rid=#id#", Page: roles_list)
                }
            }
        }
    }.Where(#where#).Order("deleted ASC, id ASC").Columns("id,role_name,role_type,image_id,creator->member_id,creator->member_name,date_created,date_deleted,deleted,roles_access->rids")
    Form(panel panel-primary){
        Div(panel-body){
            Div(row){
                Div(col-md-12){
                    Div(input-group){
                        Div(input-group-addon mt-sm){
                            LangRes(name)
                        }
                        Input(Name: search, Value: #search#)
                        Div(input-group-btn){
                            Button(Body: Em(Class: fa fa-search), Class: btn btn-default, Page: roles_list, PageParams: "search=Val(search)")
                        }
                    }
                }
            }
            Div(table-responsive){
                Table(Source: src_roles, Columns: "$id$=_id,$name$=_name,$type$=_type,$creator$=_creator,$created$ / $deleted$=_date,$status$=_status,$roles_access$=_access,=_actions")
            }
            If(GetVar(search)){
                Div(row){
                    Div(col-md-12 mt-lg){
                        Div(text-center){
                            Button(Body: $view_all$, Class: btn btn-primary, Page: roles_list)
                        }
                    }
                }
            }
        }
    }
}.Style(
    .inline {display:inline-block;}
    .breaker {word-break: break-all;}
)

Div(content-wrapper){
    
    If(#v_role_id# > 0){
        DBFind(Name: roles, Source: src_roles).Columns("role_name,role_type,deleted").Where("id=#v_role_id#").Vars(prefix)
        
        SetTitle($role$: #prefix_role_name#)
        Div(breadcrumb){
            LinkPage($roles$, roles_list)
            Span(/).Style(margin-right: 10px; margin-left: 10px;)
            Span(Class: text-muted, Body: #prefix_role_name#)
        }
        
        DBFind(Name: roles_participants, Source: src_participants).Columns("id,member->member_id,member->member_name,member->image_id,appointed->member_id,appointed->member_name,appointed->voting_id,appointed->voting_name,date_created,date_deleted,deleted").Custom(custom_id){
            If(#deleted# == 0){
                SetVar(Name: style_text, Value: "text-normal")
                SetVar(Name: style_link, Value: "text-primary")
            }.Else{
                SetVar(Name: style_text, Value: "text-muted")
                SetVar(Name: style_link, Value: "text-muted")
            }
            Span(Class: #style_text#, Body: #id#)
        }.Custom(custom_appointed){
            If(Or(#appointed.member_id#>0,#appointed.member_id#<0)){
                LinkPage(Class: #style_link# h5 text-bold, Page: profile_view, PageParams: "v_member_id=#appointed.member_id#"){
                    Span(Body: #appointed.member_name#)
                }
            }
            If(#appointed.voting_id#>0){
                LinkPage(Page: voting_view, PageParams: "vID=#appointed.voting_id#"){
                    Span(Class: h5 text-bold, Body: #appointed.voting_name#)
                }
            }
        }.Custom(custom_member){
            LinkPage(Class: #style_link# h5 text-bold, Page: profile_view, PageParams: "v_member_id=#member.member_id#"){
                If(#member.image_id#>0){
                    Image(Src: Binary().ById(#member.image_id#), Class: img-circle).Style(width: 30px; border: 1px solid #5A5D63; margin-right: 10px;)
                    Span(#member.member_name#)
                }.Else{
                    Div(){
                        Span(Em(Class: fa icon-user fa-2x)).Style(margin-right:10px;)
                        Span(#member.member_name#)
                    }.Style(display:flex; align-items:center;)
                }
            }
        }.Custom(custom_address){
            Span(Class: #style_text#, Body: Address(#member.member_id#))
        }.Custom(custom_date){
            Div(Class: #style_text# h6 m0, Body: DateTime(DateTime: #date_created#, Format: "DD.MM.YYYY HH:MI"))
            Div(Class: #style_text# h6 m0, Body: DateTime(DateTime: #date_deleted#, Format: "DD.MM.YYYY HH:MI"))
        }.Custom(custom_status){
            If(#deleted# == 0){
                Span(Class: #style_text#, Body: LangRes(active))
            }.Else{
                Span(Class: #style_text#, Body: LangRes(deleted))
            }
        }.Custom(actions){
            Div(pull-right){
                If(And(#deleted#==0,#prefix_role_type#!=3)){
                    Button(Body: Em(Class: fa fa-trash), Class: btn btn-default, Contract: Roles_Unassign, Params: "row_id=#id#", Page: roles_view, PageParams: "v_role_id=#v_role_id#").Alert(Text: $sure_want_delete$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                }
            }
        }.Where("role->id='#v_role_id#' and deleted=0").Order("deleted ASC, id ASC")
        
        Form(panel panel-primary){
            Div(panel-body){
                Table(Source: src_participants, Columns: "$id$=custom_id,$member$=custom_member,$address$=custom_address,$appointed$=custom_appointed,$appointed$ / $deleted$=custom_date,$status$=custom_status,=actions")
            }
            Div(panel-footer text-right){
                Button(Body: LangRes(back), Class: btn btn-default, Page: roles_list)
                If(#prefix_deleted#==0){
                    Button(Body: LangRes(assign), Class: btn btn-primary, Page: roles_assign, PageParams: "v_role_id=#v_role_id#")
                }
            }
        }
    }.Else{
        Div(md-12 alert alert-danger text-center){
            Span(Body: LangRes(attention))
        }
    }
}

Div(content-wrapper){
    DBFind(applications,src_app).Columns("name,id").Where("name='Basic application'").Vars(application)
    If(#v_role_id#>0){
        DBFind(roles,src_roles).Columns("id,role_name,role_type,roles_access->rids,creator->member_id").Where("id=#v_role_id#").Vars(prefix)

        If(#prefix_creator_member_id#==#key_id#){
            SetVar(manager_id,1)
        }.ElseIf(#prefix_roles_access_rids#!=""){
            DBFind(roles).Columns("id").Where("id in (#prefix_roles_access_rids#) and id=#role_id#").Vars(manager)
        }.Else{
            SetVar(manager_id,0)
        }
        SetTitle($assign$)
        Div(breadcrumb){
            LinkPage($roles$, roles_list)
            Span(/,mh-sm)
            LinkPage(#prefix_role_name#, roles_view,, "v_role_id=#v_role_id#")
            Span(/,mh-sm).($assign$,text-muted)
        }

        Div(row){
            Div(col-md-6 col-md-offset-3){
                Form(panel panel-primary){
                    Div(panel-heading){LangRes(assign)}
                    Div(panel-body){
                        Div(list-group-item){
                            Div(row){
                                Div(col-md-3 mt-sm text-right){
                                    Label(){
                                        Span(Body: LangRes(role))
                                        Span(Class: text-danger, Body:*)
                                    }
                                }
                                Div(col-md-9 mb-sm text-left){
                                    Input(Type: hidden, Name: rid, Value: #prefix_id#)
                                    SetVar(Name: var_prefix_role_name, Value: LangRes(#prefix_role_name#))
                                    Input(Name: input_prefix_role_name, Value: #var_prefix_role_name#, Disabled: true)
                                }
                            }
                            Div(row){
                                Div(col-md-3 mt-sm text-right){
                                    Label(){
                                        LangRes(type) Span(*,text-danger)
                                    }
                                }
                                Div(col-md-9 mb-sm text-left){
                                    SetVar(Name: var_prefix_role_type, Value: AppParam(App:#application_id#, Name: roles_types, Index: #prefix_role_type#))
                                    Input(Name: input_prefix_role_type, Value: #var_prefix_role_type#, Disabled: true)
                                    If(#manager_id#==0){
                                        P(Class: m0 text-danger h6, Body: LangRes(appointment_impossible))
                                    }
                                }
                            }
                            If(#manager_id#>0){
                                Div(row){
                                    Div(col-md-3 mt-sm text-right){
                                        Label(){
                                            Span(Body: LangRes(member)).(Class: text-danger, Body:*)
                                        }
                                    }
                                    Div(col-md-9 text-left){
                                        DBFind(Name: members, Source: source_members).Columns("id,member_name").Custom(custom_username){
                                            #member_name# (Address(#id#) )
                                        }
                                        Select(Name: member_id, Source: source_members, NameColumn: custom_username, ValueColumn: id)
                                    }
                                }
                            }
                        }
                    }
                    Div(panel-footer text-right){
                        Button(Body: LangRes(back), Class: btn btn-default, Page: roles_view, PageParams: "v_role_id=#v_role_id#")
                        If(#manager_id#>0){
                            Button(Body: LangRes(assign), Class: btn btn-primary, Page: roles_view, PageParams: "v_role_id=#v_role_id#", Contract: Roles_Assign, Params: "member_id=Val(member_id)").Alert(Text: $want_assign_member_to_role$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                        }
                    }
                }
            }
        }
    }.Else{
        Div(md-12 alert alert-danger text-center){
            Span(Body: LangRes(attention))
        }
    }
}

DBFind(applications).Columns("name,id").Where("name='Basic application'").Vars(application)
DBFind(roles, src_roles).Columns("role_name,id").Where("deleted=0").Order(id)

If(GetVar(role_name)){}.Else{SetVar(role_name,)}
If(GetVar(role_type)){}.Else{SetVar(role_type,1)}

If(GetVar(rid)){
    DBFind(roles).Where("id=#rid#").Columns("roles_access->rids,id,role_type,role_name").Vars(rid)
    SetVar(title,$edit_role$).(role_type,"AppParam(App:#application_id#, Name: roles_types, Index: #rid_role_type#)")

    If(#rid_roles_access_rids#!=""){
        DBFind(roles,src_roles_access).Columns("id,role_name").Where("id in (#rid_roles_access_rids#)").Order(role_name)
    }
    DBFind(roles,src_roles).Columns("id,role_name").Where("deleted=0").Order(id)
    Data(form, "Name,Res,Type,Src,Val"){
        role_name,name,text,,#role_name#
        roles_types,type,app_param,,#rid_role_type#
        roles_managers,roles_managers,rids,src_roles,
    }
}.Else{
    AppParam(App:#application_id#, Name: roles_types, Source: app_roles_types)
    SetVar(rid,0).(title,$new_role$)
    Data(form, "Name,Res,Type,Req,Src,Val"){
        role_name,name,input,*,"",#role_name#
        role_type,type,select,*,app_roles_types,#role_type#
    }
}
SetTitle(#title#)
If(And(#role_name#=="",#rid_id#>0)){
    SetVar(role_name,#rid_role_name#).(role_type,#rid_role_type#)
}

Div(content-wrapper){
    Div(breadcrumb){
        LinkPage($roles$, roles_list)
        Span(/).Style(margin-right: 10px; margin-left: 10px;)
        Span(Class: text-muted, Body: #title#)
    }
    Div(container){
        Div(row){
            Div(col-md-8 col-md-offset-2){
                Form(panel panel-primary){
                    Div(panel-heading){#title#}
                    Div(panel-body){
                        ForList(form){
                            Div(row form-group){
                                Div(col-md-3 mt-sm text-right){
                                    Label(For:#Name#){
                                        LangRes(#Res#)
                                        If(#Req#=="*"){
                                            Span(*,text-danger)
                                        }
                                    }
                                }
                                Div(col-md-9 text-left){
                                    If(#Type#==input){
                                        Input(Name: #Name#, Value:#Val#).Validate(minLength:3, maxLength:30)
                                        InputErr(Name: #Name#, minLength: $validate_role_name$, maxLength: $validate_role_name$)
                                    }.ElseIf(#Type#==text){
                                        Div(text-muted mt-sm){#Val#}
                                    }.ElseIf(#Type#==app_param){
                                        Div(text-muted mt-sm){
                                            AppParam(App:#application_id#, Name: #Name#, Index: #Val#)
                                        }
                                    }.ElseIf(#Type#==select){
                                        Select(Name: #Name#, Source: #Src#, NameColumn: name, ValueColumn: id, Value:#Val#)
                                    }.ElseIf(#Type#==rids){
                                        Div(input-group mb-sm){
                                            Select(Name:#Name#, Source:#Src#, NameColumn:role_name, ValueColumn: id)
                                            Div(input-group-btn){
                                                Button(Class: btn btn-primary fa fa-plus, Page: roles_create, PageParams:"role_name=Val(role_name),role_type=Val(role_type),rid=#rid#", Contract:Roles_AccessManager, Params:"Rid=#rid#,ManagerRid=Val(#Name#),Action=add")
                                            }
                                        }
                                        ForList(src_roles_access){
                                            If(#id#==#role_id#){
                                                Button(Class: btn btn-info mr-sm mb-sm, Page: roles_create, PageParams:"role_name=Val(role_name),role_type=Val(role_type),rid=#rid#", Contract:Roles_AccessManager, Params:"Rid=#rid#,ManagerRid=#id#,Action=remove", Body: Span(Class: fa fa-remove) #role_name#).Alert(Text: $want_delete_your_role_from_manager$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                                            }.Else{
                                                Button(Class: btn btn-default mr-sm mb-sm, Page: roles_create, PageParams:"role_name=Val(role_name),role_type=Val(role_type),rid=#rid#", Contract:Roles_AccessManager, Params:"Rid=#rid#,ManagerRid=#id#,Action=remove"){
                                                    Span(Class: fa fa-remove)
                                                    #role_name#
                                                }
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                    Div(panel-footer text-right clearfix){
                        Div(pull-left){
                            Button(Body: LangRes(back), Class: btn btn-default, Page: roles_list)
                        }
                        If(#rid_id#>0){}.Else{
                            Button(Body: LangRes(create), Class: btn btn-primary, Page: roles_list, Contract: Roles_Create)
                        }
                    }
                }
            }
        }
    }
}

Div(content-wrapper){
    SetTitle($role_notifics$)
    AddToolButton(Title: $send$, Page: notifications_roles_send, Icon: icon-plus)

    If(GetVar(Search)){
        SetVar(Where,"notification->type='2' and recipient->role_name='#Search#'")
    }.Else{
        SetVar(Where,"notification->type='2'").(Search,)
    }

    DBFind(Name: notifications, Source: src_notifications).Limit(20).Columns("id,recipient->role_id,recipient->role_name,recipient->image_id,sender->type,sender->member_id,sender->member_name,sender->role_id,sender->role_name,notification->icon,page_name,page_params,date_created,date_closed,date_start_processing,processing_info->member_name,closed").Custom(_id){
        If(#closed# == 0){
            SetVar(style_text,"text-normal").(style_link,"text-primary")
        }.Else{
            SetVar(style_text,"text-muted").(style_link,"text-muted")
        }
        Span(Class: #style_text#, Body: #id#)
    }.Custom(_recipient){
        LinkPage(Class: #style_link# t5 text-bold, Page: roles_view, PageParams: "v_role_id=#recipient.role_id#"){
            If(#recipient.image_id#>0){
                Image(Src: Binary().ById(#recipient.image_id#) "#img_data#", Class: mr-sm).Style(width: 30px; border: 1px solid #5A5D63;)
                #recipient.role_name#
            }.Else{
                Div(){
                    Span(Em(Class: fa icon-settings fa-2x)).Style(margin-right:10px;)
                    Span(#recipient.role_name#)
                }.Style(display:flex; align-items:center;)
            }
        }
    }.Custom(_sender){
        If(#sender.type#==1){
            LinkPage(Class: #style_link# h5 text-bold, Page: profile_view, PageParams: "v_member_id=#sender.member_id#"){#sender.member_name#}
        }
        If(#sender.type#==2){
            LinkPage(Class: #style_link# h5 text-bold, Page: roles_view, PageParams: "v_role_id=#sender.role_id#"){#sender.role_name#}
        }
    }.Custom(_icon){
        Span(Class: fa #notification.icon# fa-2x #style_text#)
    }.Custom(_page){
        LinkPage(Class: #style_link# t5 text-bold, Page: #page_name#, PageParams: "notific_id=#id#"){
            #page_name#
        }
    }.Custom(_date){
        Div(#style_text# t6){DateTime(DateTime: #date_created#, Format: "DD.MM.YYYY HH:MI")}
        Div(#style_text# t6){DateTime(DateTime: #date_closed#, Format: "DD.MM.YYYY HH:MI")}
    }.Custom(_processing){
        Div(Class: #style_text# t6){
            If(#date_start_processing# == NULL){
                $not_started$
            }.Else{
                Span(LangRes(started), mr-sm) #processing_info.member_name# 
                Span(LangRes(at), mr-sm)
                DateTime(DateTime: #date_start_processing#, Format: "DD.MM.YYYY HH:MI")
            }
        }
    }.Custom(_status){
        If(#closed# == 0){
            Span(Class: #style_text#){
                If(#date_start_processing# == NULL){
                    LangRes(active)
                }.Else{
                    LangRes(processing)
                }
            }
        }.Else{
            Span(Class: #style_text#, Body: LangRes(closed))
        }
    }.Custom(_actions){
        Div(text-right){
            If(#closed# == 0){
                If(#date_start_processing# == NULL){
                    Button(Class: btn btn-default fa fa-play, Contract: notifications_Process, Params: "notific_id=#id#", Page: notifications_roles).Alert(Text: $want_start_processing_notification$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                }.Else{
                    Button(Class: btn btn-default fa fa-trash, Contract: notifications_Close, Params: "notific_id=#id#", Page: notifications_roles).Alert(Text: $want_close_notification$, ConfirmButton: $yes$, CancelButton: $no$, Icon: question)
                }
            }
        }
    }.Where(#Where#).Order("closed ASC, id DESC")

    Form(panel panel-primary){
        Div(panel-body){
            Div(row){
                Div(col-md-12){
                    Div(input-group){
                        Div(input-group-addon){
                            LangRes(recipient)
                        }
                        Input(Name: Search, Value: #Search#)
                        Div(input-group-btn){
                            Button(Class: btn btn-default fa fa-search, Page: notifications_roles, PageParams: "Search=Val(Search)")
                        }
                    }
                }
            }
            Div(row){
                Div(col-md-12){
                    Table(Source: src_notifications, Columns: "$id$=_id,$icon$=_icon,$recipient$=_recipient,$page_name$=_page,$sender$=_sender,$processing$=_processing,$sent$/$сlosed$=_date,$status$=_status,=_actions")
                }
            }
            If(GetVar(Search)){
                Div(row){
                    Div(col-md-12 mt-lg){
                        Div(text-center){
                            Button(Body: $view_all$, Class: btn btn-primary, Page: notifications_roles)
                        }
                    }
                }
            }
        }
    }
}

