contract RoleConditions {
    data{
        role string
    }
    conditions {
        if !DBFind("roles_participants").Where("role->name=? and member->member_id=? and deleted=0", $role, $key_id).Row() {
            warning Sprintf("This action allowed only for role: %v", $role)
        }
    }
}

contract Roles_CheckAccess {
    data {
        rid int
    }
    conditions {
        var role map
        role = DBFind("roles").Columns("id,role_name,deleted,roles_access->rids,role_type,creator->member_id,company_id").Where("id=?", $rid).Row()
        if !role {
            warning Sprintf("%v: Role not found", $this_contract)
        }

        if role["creator.member_id"] == $key_id {
            return
        }

        var rids rawRids array, i int
        rawRids = Split(role["roles_access.rids"], ",")
        while i < Len(rawRids){
            if rawRids[i] > 0 {
                rids = Append(rids, rawRids[i])
            }
            i=i+1
        }
        i=0
        while i<Len(rids){
            if rids[i] == $role_id {
                return
            }
            i=i+1
        }

        if role["role_type"] == 4 {
            // company role
            if role["company_id"] == $key_id {
                return
            }else{
                warning "Sorry, you are not the creator of this company"
            }
        }
        warning Sprintf("Sorry, you don't have permission to change this role (%q)", role["role_name"])
    }
}

contract Roles_AccessManager {
    data {
        Action string
        Rid int
        ManagerRid string "optional"
    }
    func updateAccess(ra string){
        var roles_access map
        roles_access["rids"] = ra
        DBUpdate("roles", $Rid, "roles_access", roles_access)
    }
    conditions {
        Roles_CheckAccess("rid", $Rid)

        $ManagerRid = Int($ManagerRid)
        $role = DBFind("roles").Columns("id,roles_access->rids").Where("id=?", $Rid).Row()
        if !$role {
            warning "Role not found"
        }
        if $ManagerRid > 0{
            if !DBFind("roles").Where("id=?", $ManagerRid).One("id") {
                warning "Role not found"
            }
        }
        var ids array, i int
        $ids = ids
        ids = Split($role["roles_access.rids"],",")
        while i < Len(ids){
            if ids[i] > 0 {
                $ids = Append($ids, ids[i])
            }
            i=i+1
        }
    }

    action {
        if $Action == "clean"{
            updateAccess("")
        }
        if $ManagerRid > 0{
            var i int, idsNew array
            if $Action == "remove"{
                while i<Len($ids){
                    if $ids[i] != $ManagerRid{
                        idsNew = Append(idsNew, $ids[i])
                    }
                    i=i+1
                }
                updateAccess(Join(idsNew, ","))
            }
            if $Action == "add"{
                var double bool
                while i<Len($ids){
                    if $ids[i] == $ManagerRid{
                        double = true
                    }
                    i=i+1
                }
                if !double {
                    $ids = Append($ids, $ManagerRid)
                }
                updateAccess(Join($ids, ","))
            }
        }
    }
}

contract Roles_Assign {
    data {
        rid int
        member_id int
    }

    conditions {
        $role_map = DBFind("roles").Columns("id,deleted,role_type,role_name,image_id,creator->member_id").Where("id=?", $rid).Row()
        if !$role_map {
            warning "Role not found"
        }

        $member_map = DBFind("members").Where("id=?", $member_id).Row()
        if !$member_map {
            warning "Member not found"
        }

        $appointer_map = DBFind("members").Where("id=?", $key_id).Row()
        if !$appointer_map {
            warning "Appointer not found"
        }

        if $role_map["deleted"] == 1 {
            warning "This role has been deleted. The appointment of members is impossible"
        }

        var specialRules map prev_contract string stack array
        stack = JSONDecode(JSONEncode($stack))
        prev_contract = stack[0]
        if Len(stack)>2{
            prev_contract = stack[Len(stack)-2]
        }
        specialRules["@1sysparams_AcceptValidator"] = "Validator"
        specialRules["@1sysparams_DelegateRefresh"] = "Delegate"
        specialRules["@1sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["@1voting_TemplateRun"] = "Investor with voting rights"
        specialRules["@1tokenrefund_Start"] = "Investor with voting rights"
        if specialRules[prev_contract] != $role_map["role_name"] {
            Roles_CheckAccess("rid", $rid)
        }

        $assigned = DBFind("roles_participants").Where("role->id=? and member->member_id=? and deleted=0", $rid, $member_id).One("id")
    }

    action {
        if $assigned {
            // do not re-assign and do not warning because this contract may be called for other
            return
        }
        var role member appointed map
        role["id"] = $role_map["id"]
        role["type"] = $role_map["role_type"]
        role["name"] = $role_map["role_name"]
        role["image_id"] = $role_map["image_id"]

        member["member_id"] = $member_map["id"]
        member["member_name"] = $member_map["member_name"]
        member["image_id"] = $member_map["image_id"]

        appointed["member_id"] = $appointer_map["id"]
        appointed["member_name"] = $appointer_map["member_name"]
        appointed["image_id"] = $appointer_map["image_id"]

        DBInsert("roles_participants", "role,member,appointed,timestamp date_created", role, member, appointed, $block_time)
    }
}

contract Roles_Create {
    data {
        role_name string
        role_type int
        company_id string "optional"
    }

    conditions {
        $company_id = Int($company_id)
        // system role
        if $role_type == 3 {
            warning "System role can not be created"
        }

        // company role
        if $role_type == 4 {
            // check param
            if $company_id == 0 {
                warning "On this page you can not create a role for the company. Go to the company's view page to create a role"
            }

            // check creator of the company
            if $company_id != $key_id {
                warning "Sorry, you are not the creator of this company"
            }
        }

        $member_map = DBFind("members").Where("id=?", $key_id).Row()
        if !$member_map {
            warning "You are not a member of the ecosystem"
        }
    }

    action {
        var creator map
        creator["member_id"] = $member_map["id"]
        creator["member_name"] = $member_map["member_name"]
        creator["image_id"] = $member_map["image_id"]

        if $role_type != 4 {
            $company_id = 0
        }
        $result = DBInsert("roles", "role_name,role_type,creator,timestamp date_created,company_id", $role_name, $role_type, creator, $block_time, $company_id)
    }
}

contract Roles_Unassign {
    data {
        row_id int
    }

    conditions {
        // check record
        var assigned role map
        assigned = DBFind("roles_participants").Columns("id,role->id").Where("id=$", $row_id).Row()
        if !assigned {
            warning "Participant not found"
        }

        role = DBFind("roles").Columns("id,creator->member_id,role_name").Where("id=?", assigned["role.id"]).Row()
        if !role {
            warning "Role not found in the roles registry"
        }

        var specialRules map prev_contract string stack array
        stack = JSONDecode(JSONEncode($stack))
        prev_contract = stack[0]
        if Len(stack)>2{
            prev_contract = stack[Len(stack)-2]
        }
        specialRules["@1sysparams_AcceptValidator"] = "Candidate for validators"
        specialRules["@1sysparams_DelegateRefresh"] = "Delegate"
        specialRules["@1sysparams_CandidateValidatorRequest"] = "Candidate for validators"
        specialRules["@1sysparams_InvestorRefresh"] = "Investor with voting rights"
        specialRules["@1tokenrefund_Start"] = "Investor with voting rights"
        specialRules["@1sysparams_ValidatorResign"] = "Validator"

        if specialRules[prev_contract] != role["role_name"] {
            Roles_CheckAccess("rid", Int(role["id"]))
        }
    }

    action {
        // delete the member
        DBUpdate("roles_participants", $row_id, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract Roles_Restore {
    data {
        rid int
    }

    conditions {        
        Roles_CheckAccess("rid", $rid)
    }

    action {
        DBUpdate("roles", $rid, "deleted", 0)
    }
}

contract Roles_Del {
    data {
        rid int
    }

    conditions {
        Roles_CheckAccess("rid", $rid)
    }

    action {
        var participants array
        participants = DBFind("roles_participants").Columns("id").Where("role->id=? and deleted=0", $rid)

        // delete all participants
        var i int
        while i < Len(participants) {
            var p map
            p = participants[i]
            Roles_Unassign("row_id", Int(p["id"]))
            i = i + 1
        }

        DBUpdate("roles", $rid, "deleted,timestamp date_deleted", 1, $block_time)
    }
}

contract notifications_Process {
    data {
        notific_id int
    }

    conditions {
        $notify = DBFind("notifications").Columns("recipient->role_id,sender->type,date_start_processing").Where("id=?", $notific_id).Row()
        if !$notify {
            warning "Notification not found"
        }

        if $notify["sender.type"] == 2{
            if $notify["date_start_processing"] != "" {
                warning "Sorry, processing of this notification has already begun"
            }
            if !DBFind("roles_participants").Where("role->id=? and member->member_id=? and deleted=0", Int($notify["recipient.role_id"]), $key_id).One("id") {
                warning "Sorry, you are not part of the role for which this notification is intended"
            }
        }else{
            if $notify["sender.type"] != 1 {
                warning "Notification type not found"
            }
        }

        $member = DBFind("members").Where("id=?", $key_id).Row()
        if !$member {
            warning "Member not found"
        }
    }

    action {
        var member_info map
        member_info["member_id"] = $member["id"]
        member_info["member_name"] = $member["member_name"]
        member_info["image_id"] = $member["image_id"]

        DBUpdate("notifications", $notific_id, "timestamp date_start_processing,processing_info", $block_time, JSONEncode(member_info))
    }
}

contract notifications_Close {
    data {
        notific_id int
    }

    conditions {
        $notify = DBFind("notifications").Columns("id,date_start_processing,processing_info->member_id,closed,sender->type").Where("id=$", $notific_id).Row()
        if !$notify {
            warning "Notification not found"
        }
        if $notify["sender.type"] == 2{
            //role has processing check
            if $notify["date_start_processing"] == "" {
                warning "Sorry, processing of this notification has not yet begun"
            }

            if $notify["processing_info.member_id"] != $key_id {
                warning "Sorry, processing of this notice began another member"
            }

            if $notify["closed"] > 0 {
                warning "Sorry, this notification has already been closed before"
            }
        }else {
            if $notify["sender.type"] != 1 { // not single
                warning "Notification type not found"
            }
        }
    }

    action {
        if $notify["sender.type"] == 1{
            notifications_Process("notific_id", $notific_id)
        }

        DBUpdate("notifications", $notific_id, "timestamp date_closed,closed", $block_time, 1)
        //DBUpdate("notifications", $notific_id, "closed", 0)
    }
}

contract notifications_Send {
    data {
        member_id int "optional"
        rid int "optional"
        closure_type int "optional"

        sender int
        icon_name string "optional"
        text_header string
        text_body string "optional"
        page_name string
        params_map string // json encoded expected
        eco string "optional"
    }
    func sendNotify(recipient map){
        var notification map
        notification["header"] = $text_header
        notification["type"] = $notify_type
        notification["icon"] = $icon_name
        notification["body"] = $text_body
        var sender map
        if $sender == 1 && $senderMember {
            // send from member
            sender["type"] = $sender
            sender["member_id"] = $senderMember["id"]
            sender["member_name"] = $senderMember["member_name"]
            sender["image_id"] = $senderMember["image_id"]
        }else{
            if $sender == 2 && $senderRole{
                // send from role
                sender["type"] = $sender
                sender["role_id"] = $senderRole["id"]
                sender["role_name"] = $senderRole["role_name"]
                sender["image_id"] = $senderRole["image_id"]
            }
        }
        var notifications string
        if $eco > 0 {
            notifications = Sprintf("@%v_notifications", $eco)
        }else{
            notifications = "notifications"
        }        

        DBInsert(notifications, "recipient,sender,notification,page_name,page_params,timestamp date_created",
        recipient, sender, notification, $page_name, $params_map, $block_time)
    }
    func sendToMember(memberId int){
        var table_members string
        if $eco > 0 {
            table_members = Sprintf("@%v_members", $eco)
        }else{
            table_members = "members"
        } 

        var member map
        member = DBFind(table_members).Where("id=?", memberId).Row()
        if !member {
            warning Sprintf("%v:  Member not found (%v)", $this_contract, memberId)
        }
        var recipient map
        recipient["member_id"] = member["id"]
        recipient["member_name"] = member["member_name"]
        recipient["image_id"] = member["image_id"]

        sendNotify(recipient)
    }

    func sendToRole(roleId int){
        var role map
        role = DBFind("roles").Where("id=?", roleId).Row()
        var recipient map
        recipient["role_id"] = role["id"]
        recipient["role_name"] = role["role_name"]
        recipient["image_id"] = role["image_id"]

        sendNotify(recipient)
    }

    conditions {
        $member_id = Int($member_id)
        $eco = Int($eco)
        $rid = Int($rid)
        if Size($icon_name) < 1{
            $icon_name = "icon-envelope"
        }
        if $member_id != 0 {
            $notify_type = 1 // is "member"
        }else{
            if $rid > 0{
                $notify_type = 2 // is "role"
                if !DBFind("roles").Where("id=?", $rid).One("id") {
                    warning $this_contract+": Recipient role not found"
                }
                if !DBFind("roles_participants").Where("role->id=? and deleted=0", $rid).One("id") {
                    warning $this_contract+": Recipient role does not contain members"
                }
            } else{
                warning $this_contract+": Notification type not defined"
            }
        }
        $senderMember = nil
        $senderRole = nil
        if $sender == 1 {
            $senderMember = DBFind("members").Where("id=?", $key_id).Row()
            if !$senderMember {
                warning $this_contract+": Sender not member"
            }
        }else{
            if $sender == 2 {
                $senderRole = DBFind("roles").Where("id=?", $role_id).Row()
                if !$senderRole {
                    warning $this_contract+": Sender role not found"
                }
            }else{
                warning $this_contract+": Sender invalid"
            }
        }
    }
    action {
        if $member_id != 0 {
            sendToMember($member_id)
        }else{
            if $rid > 0 {
                if $closure_type == 1 {
                    // Single closure: one begins the process - the others do not see
                    // Одиночное закрытие: все имеющие роль получают оповещение, но как только один из них запустит процесс работы с оповещением оно скроется от остальных
                    sendToRole($rid)
                } else {
                    // Multiple closure: to each his own notification
                    // Множественное закрытие: каждый участник роли получает свое одиночное оповещение
                    $notify_type = 1
                    var participants array
                    participants = DBFind("roles_participants").Columns("member->member_id").Where("role->id=? and deleted=0", $rid)
                    var i int
                    while i < Len(participants) {
                        var p map
                        p = participants[i]
                        sendToMember(Int(p["member.member_id"]))
                        i=i+1
                    }
                }
            }
        }
    }
}

