contract paper_Accept {
    data {
        PaperId int
        NotificId int
        Amount money
    }
    func signAccept(){
        var signature string
        if $isBuyer {
            signature = "signature_buyer"
        }
        if $isOwner {
            signature = "signature_owner"
        }
        if signature {
            DBUpdate("@3_form_of_listing2", $PaperId, signature, 1)
        }
    }
    func isFullSigned() bool{
        if DBFind("form_of_listing2").Ecosystem(3).Where("id=? and signature_fin1=1 and signature_fin2=1 and signature_buyer=1 and signature_owner=1", $PaperId).One("id") {
            return true
        }
        return false
    }
    func changeOwner(){
        DBUpdate("@3_form_of_listing2", $PaperId, "issuer,dealer,signature_fin1,signature_fin2,signature_owner,signature_buyer", $paper["dealer"], 0, 0, 0, 0, 0)
    }
    func transferAmount(){
        if $paper["dealer"] != 0 {
            DBUpdate("@1_keys", Int($paper["dealer"]), "-deposit,-amount", $Amount, $Amount)
            DBUpdate("@1_keys", Int($paper["issuer"]), "+amount", $Amount)
        }
    }
    conditions {
        $paper = DBFind("form_of_listing2").Ecosystem(3).Where("id=?", $PaperId).Row()
        if !$paper {
            warning "Paper not found"
        }
        $isBuyer = false
        $isOwner = false
        if $paper["dealer"] == $key_id {
            $isBuyer = true
        }
        if $paper["issuer"] == $key_id {
            $isOwner = true
        }
        if !($isOwner || $isBuyer){
            warning "Denied. You can not accept it"
        }
    }
    action {
        signAccept()
        if isFullSigned() {
            transferAmount()
            changeOwner()

            var params_map map
            params_map["offer_id"] = $PaperId
            notifications_Send("member_id,sender,text_header,text_body,page_name,params_map", Int($paper["dealer"]), 1,"Commercial Paper","Commercial Paper","ViewOffer",params_map)
            notifications_Send("member_id,sender,text_header,text_body,page_name,params_map", Int($paper["issuer"]), 1,"Commercial Paper","Commercial Paper","ViewOffer",params_map)
        }
        notifications_Close("notific_id", $NotificId)
    }
}

contract paper_Reject {
    data {
        PaperId int
        NotificId int
    }
    func signReject(){
        var signature string
        if $isBuyer {
            signature = "signature_buyer"
        }
        if $isOwner {
            signature = "signature_owner"
        }
        if signature {
            DBUpdate("@3_form_of_listing2", $PaperId, signature, -1)
        }
    }
    func unholdDeposit(){
        var amount money
        amount = Int($paper["payment"]) * SysParamInt("money_digit")
        DBUpdate("@1_keys", Int($paper["dealer"]), "-deposit", amount)
    }
    func isValidForUnhold(){
        if $isOwner {
            return
        }
        if DBFind("form_of_listing2").Ecosystem(3).Where("id=? and (signature_buyer=-1 or signature_owner=-1)", $PaperId).Row(){
            return
        }
        var amount money
        amount = Int($paper["payment"]) * SysParamInt("money_digit")
        DBUpdate("@1_keys", Int($paper["dealer"]), "-deposit", amount)
    }
    conditions {
        $paper = DBFind("form_of_listing2").Ecosystem(3).Where("id=?", $PaperId).Row()
        if !$paper {
            warning "Paper not found"
        }
        $isBuyer = false
        $isOwner = false
        if $paper["dealer"] == $key_id {
            $isBuyer = true
        }
        if $paper["issuer"] == $key_id {
            $isOwner = true
        }
        if !($isOwner || $isBuyer){
            warning "Denied. You can not reject it"
        }
        if $paper["dealer"] == $paper["issuer"] {
            warning "Denied. Offer out of date"
        }
    }
    action {
        signReject()
        if isValidForUnhold() {
            unholdDeposit()
        }
        notifications_Close("notific_id", $NotificId)
    }
}

