    Div(col-md-6 text-left){
    ForList(ajman_contract_form){
        Div(row){
            Div(col-md-2){
                Label(Body: LangRes(#name#), For: #name#)
            }
            SetVar(add_class, "col-md-9")
            If(#type# == view){
                SetVar(add_class, "col-md-9 text-right")
            }
            If(Or(#type# == textarea, #type#==view_text)){
                SetVar(add_class, "col-md-12")
            }
            Div(#add_class#){
                If(#type#==input){
                    Div(list-group){
                        If(#disable#>0){
                            Input(Name:#name#, Value:#value#, Disabled: #disable#)
                        }.Else{
                            Input(Name:#name#, Value:#value#)
                        }
                    }
                }
                If(#type#==textarea){
                    Div(list-group){
                        Input(Name:#name#, Type:textarea, Value:#value#, Disabled: #disable#)
                    }
                }
                If(#type#==password){
                    Div(list-group){
                        Input(Name:#name#, Type:password, Value:#value#, Disabled: #disable#)
                    }
                }
                If(#type#==datetime){
                    SetVar(date, DateTime(#value#, "YYYY-MM-DD"))
                    Div(list-group){
                        If(#disable#>0){
                            Input(Name:#name#, Type:date, Value: #date#, Disabled: #disable#)
                        }.Else{
                            Input(Name:#name#, Type:date, Value: #date#)
                        }
                    }
                }
                If(#type#==file){
                    Div(list-group){
                        Input(Name:#name#, Type:file, Disabled: #disable#)
                    }
                }
                If(#type#==checkbox){
                    If(#value# == 1){
                        SetVar(checked, true)
                    }
                    Div(list-group){
                        If(#disable#>0){
                            Input(Name:#name#, Type:checkbox, Value: #checked#, Disabled: #disable#)
                        }.Else{
                            Input(Name:#name#, Type:checkbox, Value: #checked#)
                        }
                    }
                }
                If(#type#==radio){
                    Div(list-group){
                        RadioGroup(Name:#name#, Source: #src#, NameColumn:name, ValueColumn: id, Value: #value#, Disabled: #disable#)
                    }
                }
                If(#type#==select){
                    Div(list-group){
                        Select(Name:#name#, Source: #src#, NameColumn:name, ValueColumn: id, Value: #value#, Disabled: #disable#)
                    }
                }
                If(#type#==json_select){
                    Div(list-group){
                        Select(Name:#name#, Source: #src#, NameColumn:value, ValueColumn: key, Value: #value#, Disabled: #disable#)
                    }
                }
                If(#type#==view_text){
                    Span(#value#)
                }
            }
        }
    }
}
