contract ajman_ContractNew {
    data {
        id int "optional" // contract id for edit
        btn string //which button user click{submit;save;sign}
        //vars from form
        start_date string "date"
        end_date string "date"
        with_parking string "optional"
        no_of_renewal int "optional"
        terms_and_conditions_en string "optional"
        terms_and_conditions_ar string "optional"
        //additional vars
        property_id int "optional"
        unit_id int "optional"
        owner_id int "optional"
		notific_id string "optional"
    }
    conditions {
        if !$property_id && !$unit_id {
            error "Property or Unit must be selected"
        }
        if $with_parking == "true" {
            $with_parking = "1"
        }else{
            $with_parking = "0"
        }
        if !$no_of_renewal {
            $no_of_renewal = 0
        }
    }
    action {
		$notific_id = Int($notific_id)
		if($notific_id > 0){
            Notifications_Roles_Processing("notific_id", $notific_id)
			Notifications_Roles_Close("notific_id", $notific_id)
		}
        $start_date = "timestamp " + $start_date
        $end_date = "timestamp " + $end_date
        $duration = 0
        $landlord_id = 1
        $contract_type = 3//Residential
        $company_id = 0
        $application_number = 0
        $license_number = 0
        $role = DBRow("roles").WhereId($role_id)
        if $role["role_name"] == "Tenant Company Representative" {
            $contract_type = 4//Commercial
            $company = DBRow("companies").Where("license_owner_id = ?", $key_id)
            $company_id = $company["id"]
        }
        if $unit_id > 0 {
            $Property = DBRow("units").WhereId($unit_id)
        }else{
            $Property = DBRow("properties").WhereId($property_id)
        }
        $total_rent_amount = $Property["total_rent_amount"]
        $security_deposit = $Property["security_deposit"]
        $no_of_installments = $Property["no_of_installments"]
        if $id > 0 {
            $Contract = DBRow("property_contracts").Where("id = ?", $id)
            $contract_id = $Contract["id"]
            $tenant_id = $Contract["tenant_id"]
            $owner_id = $Contract["owner_id"]
            $status_signature = 0
            //if !(($key_id == $Contract["tenant_id"] && $Contract["status_signature"] == 1) || ($key_id == $Contract["owner_id"] && ($Contract["status_signature"] == 2 || $Contract["status_signature"] == 3)) {
            //    error "You can't edit this contract"
            //}
            if $key_id == Int($Contract["tenant_id"]) {
                if $btn == "save" {
                    $status_signature = 1
                }
                if $btn == "submit" {
                    $status_signature = 2
                }
            }
            if $key_id == Int($Contract["owner_id"]) {
                if $btn == "save" {
                    $status_signature = 3
                }
            }
            if $status_signature > 0{
                DBUpdate("property_contracts", $id, "total_rent_amount,security_deposit,no_of_installments,start_date,end_date,with_parking,no_of_renewal,terms_and_conditions_en,terms_and_conditions_ar,date_create,duration,type,status_signature", $total_rent_amount, $security_deposit, $no_of_installments, $start_date, $end_date, $with_parking, $no_of_renewal, $terms_and_conditions_en, $terms_and_conditions_ar,"NOW()",$duration, $contract_type, $status_signature)
            }
        }else{
            $tenant_id = $key_id
            if $btn == "submit" {
                $status_signature = 2//Request  
            }
            if $btn == "save" {
                $status_signature = 1//Created
            }
            $contract_id = DBInsert("property_contracts", "property_id,unit_id,company_id,license_number,application_number,tenant_id,owner_id,landlord_id,total_rent_amount,security_deposit,no_of_installments,start_date,end_date,with_parking,no_of_renewal,terms_and_conditions_en,terms_and_conditions_ar,date_create,duration,type,status_signature,status", $property_id, $unit_id, $company_id, $license_number, $application_number, $tenant_id, $owner_id, $landlord_id, $total_rent_amount, $security_deposit, $no_of_installments, $start_date, $end_date, $with_parking, $no_of_renewal, $terms_and_conditions_en, $terms_and_conditions_ar,"NOW()",$duration,$contract_type,$status_signature,"0")
        }
        var params map
        params["contract_id"] = $contract_id
        if $btn == "submit" {
            Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", $owner_id, 1, "fa-check", "Contract", "New contract", "ajman_contract_add", params)
        }
    }
}

contract ajman_SignContract {
    data {
        id int
        btn string
        notific_id string "optional"
        terms_and_conditions_en string "optional"
        terms_and_conditions_ar string "optional"
    }
    conditions {
        $Contract = DBRow("property_contracts").Where("remove = ? and id = ? and (owner_id = ? or tenant_id = ?)", 0, $id, $key_id, $key_id)
        if !$Contract {
            error "Contract was removed"
        }
    }
    action {
        $notific_id = Int($notific_id)
		if($notific_id > 0){
            Notifications_Roles_Processing("notific_id", $notific_id)
			Notifications_Roles_Close("notific_id", $notific_id)
		}
        var params map
        params["contract_id"] = $id
        $status_signature = 0
        $notification_page = "ajman_contract_add"
        var user string
        if Int($Contract["status_signature"]) >= 2 {
            if $Contract["owner_id"] == $key_id{
                user = $Contract["tenant_id"]
                if $btn == "sign" {
                    $status_signature = 4
                    $date_field = "owner_signature_date"
                    $notification_body = "Owner sign contract"
                }else{
                    $status_signature = 6
                    $notification_body = "Owner decline contract"
                }
            }
        }
        if Int($Contract["status_signature"]) == 4 {
            if $Contract["tenant_id"] == $key_id {
                $terms_and_conditions_en = $Contract["terms_and_conditions_en"]
                $terms_and_conditions_ar = $Contract["terms_and_conditions_ar"]
                user = $Contract["owner_id"]
                if $btn == "sign" {
                    $status_signature = 5
                    $date_field = "tenant_signature_date"
                    $notification_body = "Tenant sign contract. Need to attach documents"
                    params["step"] = 2  
                }else{
                    $status_signature = 6
                    $notification_body = "Tenant decline contract"    
                }
            }
        }
        if $status_signature > 0 {
            Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", user, 1, "fa-check", "Contract", $notification_body, $notification_page, params)
            DBUpdate("property_contracts", $id, "terms_and_conditions_en,terms_and_conditions_ar,status_signature,"+$date_field, $terms_and_conditions_en, $terms_and_conditions_ar, $status_signature, "NOW()")
        }else{
            error "You can't sign this contract"
        }
    }
}

contract ajman_ManagerActionURP {
    data {
        Action string
        MemberId int
        NotificId string "optional"

    }
    conditions {
        $ApplicationId = Int(DBFind("applications").Where("name='Ajman property'").One("id"))
        if $ApplicationId == 0 {
            warning "Application ID not found"
        }
        $managerRoleId = Int(AppParam($ApplicationId, "manager_role_id"))
        if $role_id != $managerRoleId{
            // info "You are not manager"
        }
        $member = DBFind("members").Where("id=?", $MemberId).Row()
        if !$member{
            error "Member not found"
        }
        $NotificId = Int($NotificId)
    }

    action {
        var header string
        var inform map
        inform = JSONDecode($member["member_info"])
        if $Action == "approve"{
            // 0:registrated, 1:rejected, 2:approved
            inform["status"] = 2
            header = "approved"
        }
        if $Action == "reject"{
            // 0:registrated, 1:rejected, 2:approved
            inform["status"] = 1
            header = "rejected"
        }
        DBUpdate("members", $MemberId, "member_info", inform)

        if $NotificId > 0{
            Println("Try closing notific id:", $NotificId)
            Notifications_Roles_Processing("notific_id", $NotificId)
            Notifications_Roles_Close("notific_id", $NotificId)
        }
        // user notification
        var sender int
        sender = 2 // 2:from admin
        var icon body page string
        header = Sprintf("Ajman URP: Registration %v", header)
        icon = "icon-envelope"
        body = Sprintf("Manager %v your registration", header)
        page = "ajman_user_registration"
        var params map
        params["member_id"] = $MemberId
        Notifications_Single_Send_map("member_id,sender,icon_name,text_header,text_body,page_name,params_map", $MemberId, sender, icon, header, body, page, params)
    }
}

