        }
        if $object_id < 1 {
            error "Object id can't be less than 1"
        }
        $ApplicationId = Int(DBFind("applications").Where("name='Ajman property'").One("id"))
        if $ApplicationId == 0 {
            warning "Application ID not found"
        }
        if $object_type == 1 {
            $object = DBRow("properties").WhereId($object_id)
        }
        if $object_type == 2 {
            $object = DBRow("units").WhereId($object_id)
        }
        if $object_type == 3 || $object_type == 4 {
            $object = DBRow("property_contracts").WhereId($object_id)
        }
        if $object["status"] != 0 && $object["status"] != 3 && $object["status"] != 5 {
            error "You can't attach files to this object"
        }
    }
    action {
        $notific_id = Int($notific_id)
		if($notific_id > 0){
            Notifications_Roles_Processing("notific_id", $notific_id)
			Notifications_Roles_Close("notific_id", $notific_id)
		}
        $file_id = UploadBinary("Name,Data,ApplicationId,DataMimeType", $name, $file, $ApplicationId, $fileMimeType)
        DBInsert("attachments", "object_type,object_id,name,expiry_date,notes,attachment_type,file_id", 
            $object_type, $object_id, $name, $expiry_date, $notes, $attachment_type, $file_id
        )
    }
}

contract ajman_ContractSubmit {
    data {
        id int
    }
    conditions {
        $Contract = DBRow("property_contracts").Where("remove = ? and id = ? and owner_id = ?", 0, $id, $key_id)
        if !$Contract {
            error "Contract was removed"
        }
    }
    action {
        $applications_fees = Int($Contract["total_rent_amount"]) / 50
        if $applications_fees < 400 {
            $applications_fees = 400
        } 
        $applications_fees = $applications_fees + 10
        $no_submitted = Int($Contract["no_submitted"]) + 1
        DBUpdate("property_contracts", $id, "applications_fees,status,no_submitted", $applications_fees, 2, $no_submitted)
        var params map
        params["ObjectId"] = $id
        params["ResultId"] = 1
        if $Contract["type"] == 3 {
            params["Id"] = 7
            params["ModelId"] = 2
            params["EventId"] = 100
            params["ObjectType"] = 3
        }
        if $Contract["type"] == 4 {
            params["Id"] = 8
            params["ModelId"] = 3
            params["EventId"] = 200
            params["ObjectType"] = 4
        }
        business_Process("Id,ModelId,EventId,ResultId,ObjectType,ObjectId", params["Id"],params["ModelId"],params["EventId"],params["ResultId"],params["ObjectType"],params["ObjectId"])
    }
}

