Div(container){
	Div(row){
		Div(col-md-12){
			Div(table){
				Div(table__head){
					Div(table__row){
						Div(table__cell table__cell_20){ID}
						Div(table__cell){ID Owner}
						Div(table__cell){Address}
						Div(table__cell){Name}
						Div(table__cell){Status}
						Div(table__cell){Start Date}
						Div(table__cell){Units}
						Div(table__cell){End Date}
						Div(table__cell table__cell_50){Action}
					}
				}
				ForList(mysrc){
					Div(table__row){
						Div(table__cell){#id#}
						Div(table__cell){#owner_id#}
						Div(table__cell){#address#}
						Div(table__cell){#name_english#}
						Div(table__cell){
							If(#status# > 0){
								EcosysParam(property_statuses,#status#)
							}.Else{
								Not submited
							}
						}
						Div(table__cell){DateTime(#start_date#, "YYYY-MM-DD")}
						Div(table__cell){LinkPage($units_add$,Page: units_add,PageParams: "property_id=#id#")}
						Div(table__cell){DateTime(#end_date#, "YYYY-MM-DD")}
						Div(table__cell){
							If(Or(#status# == 0,#status# == 3, #status# == 5)){
								Button(Class: btn ajman_button_darkgreen_medium, Body: Submit,Contract:business_Process,Params: "Id=20,ModelId=1,EventId=3,ResultId=1,ObjectType=1,ObjectId=#id#")
								Button(Class: btn ajman_button_blue_large,Body: Attachment, Page: ajman_attachment_add,PageParams: "object_id=#id#,object_type=1")
								Button(Class: btn ajman_button_red_medium, Body: Edit, Page: property_edit, PageParams: "prop_id=#id#")
							}.Else{
								Button(Class: btn ajman_button_darkgreen_medium disabled, Body: Submit)
								Button(Class: btn ajman_button_blue_large disabled,Body: Attachment)
								Button(Class: btn ajman_button_red_medium disabled, Body: Edit) 
							}
						}
					}
				}
			}
		}
	}
}
